interface SideIconsProps {
  showInMobile: boolean
  showInDesktop: boolean
}

interface IconsContainerProps {
  $showInMobile: boolean;
  $showInDesktop: boolean
}

const IconsContainer = styled.div<IconsContainerProps>`
  position: absolute;           
  top: 50%;                        
  transform: translate(0, -50%);
  right: 16px;
  display: ${props => props.$showInDesktop ? 'flex' : 'none' };;
  flex-direction: column;
  gap: 8px;

  @media ${device.tablet} {
    display: ${props => props.$showInMobile ? 'flex' : 'none' };
    flex-direction: row;
    top: 95%;
    left: 50%;
    right: auto;
    margin-right: -50%;
    transform: translate(-50%, -50%);
    gap: 16px;
  }
`

const Icon = styled.svg`
  transition: 0.6s;
  cursor: pointer;
  circle {
    fill: ${props => props.theme.pallete.secondary};
  }

  path {
    fill: ${props => props.theme.pallete.text};
  }

  &:hover {
    transform: translateX(-8px);
    circle {
      fill: ${props => props.theme.pallete.text};
    }

    path {
      fill: ${props => props.theme.pallete.primary};
    }
  }
  
  @media ${device.tablet} {
    circle {
      fill: #000;
    }

    path {
      fill: ${props => props.theme.pallete.primary};
    }

    &:hover {
      transform: none;

      circle {
        fill: ${props => props.theme.pallete.textOffset};
      }
    }
  }

`

